		       [{<<"width">>, _val} | _attrs], Uri, Media_type, _Width,
		       Height) ->
    decode_thumbnail_attrs(__TopXMLNS, _attrs, Uri,
			   Media_type, _val, Height);
decode_thumbnail_attrs(__TopXMLNS,
		       [{<<"height">>, _val} | _attrs], Uri, Media_type, Width,
		       _Height) ->
