	  when __TopXMLNS ==
		 <<"http://jabber.org/protocol/offline">> ->
	  decode_offline_els(__TopXMLNS, __IgnoreEls, _els, Items,
			     Purge,
			     decode_offline_fetch(__TopXMLNS, __IgnoreEls,
