		      __Els) ->
    lists:reverse(__Els);
decode_mam_result_els(__TopXMLNS, __IgnoreEls,
		      [{xmlel, _, _, _} = _el | _els], __Els) ->
    if __IgnoreEls ->
	   decode_mam_result_els(__TopXMLNS, __IgnoreEls, _els,
				 [_el | __Els]);
       true ->
	   case is_known_tag(_el, __TopXMLNS) of
	     true ->
		 decode_mam_result_els(__TopXMLNS, __IgnoreEls, _els,
				       [decode(_el, __TopXMLNS, []) | __Els]);
	     false ->
		 decode_mam_result_els(__TopXMLNS, __IgnoreEls, _els,
				       __Els)
	   end
    end;
