	decode_mam_query_els(__TopXMLNS, __IgnoreEls, _els,
			     undefined, undefined, undefined, undefined,
			     undefined, undefined),
    {Id, Xmlns} = decode_mam_query_attrs(__TopXMLNS, _attrs,
