      <<"">>
	  when __TopXMLNS ==
		 <<"http://jabber.org/protocol/muc">> ->
	  decode_muc_els(__TopXMLNS, __IgnoreEls, _els, Password,
			 decode_muc_history(__TopXMLNS, __IgnoreEls, _el));
      <<"http://jabber.org/protocol/muc">> ->
	  decode_muc_els(__TopXMLNS, __IgnoreEls, _els, Password,
			 decode_muc_history(<<"http://jabber.org/protocol/muc">>,
					    __IgnoreEls, _el));
