      <<"">>
	  when __TopXMLNS ==
		 <<"http://jabber.org/protocol/muc">> ->
	  decode_muc_els(__TopXMLNS, __IgnoreEls, _els,
			 decode_muc_password(__TopXMLNS, __IgnoreEls, _el),
			 History);
      <<"http://jabber.org/protocol/muc#owner">> ->
	  decode_muc_els(__TopXMLNS, __IgnoreEls, _els,
