      <<"">>
	  when __TopXMLNS == <<"jabber:server">>;
	       __TopXMLNS == <<"jabber:component:accept">>;
	       __TopXMLNS == <<"jabber:client">> ->
	  decode_presence_els(__TopXMLNS, __IgnoreEls, _els,
			      Status,
