	  decode_presence_els(__TopXMLNS, __IgnoreEls, _els,
			      Status,
			      decode_presence_show(<<"jabber:client">>,
						   __IgnoreEls, _el),
			      Priority, __Els);
      <<"jabber:server">> ->
	  decode_presence_els(__TopXMLNS, __IgnoreEls, _els,
			      Status,
			      decode_presence_show(<<"jabber:server">>,
						   __IgnoreEls, _el),
			      Priority, __Els);
