	  decode_presence_els(__TopXMLNS, __IgnoreEls, _els,
			      Status,
			      decode_presence_show(<<"jabber:component:accept">>,
						   __IgnoreEls, _el),
			      Priority, __Els);
      _ ->
	  decode_presence_els(__TopXMLNS, __IgnoreEls, _els,
			      Status, Show, Priority, __Els)
    end;
